import pickle

# USE THIS FILE TO GENERATE THE TXT FILE FOR ENCODER MAPPING

# Open the pickle file generated by the encoder_map.py file
with open("encoderList.pkl", "rb") as file:
    encoders = pickle.load(file)

output_path = "encoder_mappings.txt"

# Writing out the encodings to a txt file called encoder_mappings.txt
with open(output_path, "w") as f:
    for column, encoder in encoders.items():
        f.write(f"Column: {column}\n")
        f.write("-" * (8 + len(column)) + "\n")
        
        for i, cls in enumerate(encoder.classes_):
            f.write(f"{cls} -> {i}\n")
        
        f.write("\n") 